import React, { Component } from 'react'

export class NewsItem extends Component {

  

  render() {
    let {title, description, imageUrl, newsUrl , author , date, source} = this.props;
    return (
      <div className='my-3'>
       <div className="card" >
       <span class="position-absolute top-0 translate-middle badge bg-danger border border-light rounded-pill">
         {source} </span>
        <img src={!imageUrl?"https://akm-img-a-in.tosshub.com/indiatoday/News-Today-Rajdeep-Sardesai_320x240.jpg?VersionId=AH5FO98iiYC1UfkcbQR5.VdZVis4QSKf&size=240:135":imageUrl} className="card-img-top" alt="..." />
        <div className="card-body">
            <h5 className="card-title">{title} <span className='badge bg-secondary'>New</span></h5>
            <p className="card-text">{description}</p>
            <p className="card-text"><small className="text-muted">By {!author?"unknown":author} on {new Date(date).toGMTString()}</small></p>
            <a rel="noreferrer" href={newsUrl}  target="_blank" className="btn btn-sm btn-dark">Read More</a>
        </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
